mapper.select:

select 

from ad_activity_adseat
where id = #{id,jdbcType=INTEGER}

select

from ad_activity_adseat
where activity_id = #{activityId,jdbcType=INTEGER}
and ad_seat_id = #{seatId,jdbcType=INTEGER}

select
	count(1)
from ad_activity_adseat a
LEFT JOIN ad_seat_info c ON a.ad_seat_id = c.id
where 1=1

  and a.activity_id = #{activityId,jdbcType=INTEGER}


  and c.city = #{cityId,jdbcType=INTEGER}


  and c.media_type_parent_id = #{mediaTypeParentId,jdbcType=INTEGER}


  and a.update_time <= DATE_FORMAT(#{updateTime,jdbcType=TIMESTAMP},'Y%-%m-%d %H:%i:%s')

SELECT a.id id, a.monitor_start monitorStart, a.monitor_end monitorEnd, (SELECT COUNT(1) from ad_monitor_task b WHERE b.activity_id = #{activityId,jdbcType=INTEGER} AND b.activity_adseat_id = a.id AND b.status = 4 AND b.problem_status = 3) problem_count, c.id info_seat_id, c.name info_name, c.province info_province, c.city info_city, c.region info_region, c.street info_street, c.road info_road, c.location info_location, c.ad_size info_adSize, c.lon info_lon, c.lat info_lat, c.map_standard info_mapStandard, c.ad_area info_adArea, c.contact_name info_contactName, c.contact_cell info_contactCell, c.media_type_parent_id info_mediaTypeParentId, c.media_type_id info_mediaTypeId, c.unique_key info_uniqueKey, c.memo info_memo, d.media_name mediaName from ad_activity_adseat a LEFT JOIN ad_seat_info c ON a.ad_seat_id = c.id LEFT JOIN ad_media d ON a.media_id = d.id WHERE 1=1
and a.activity_id = #{activityId,jdbcType=INTEGER}
and c.city = #{cityId,jdbcType=INTEGER}
and c.media_type_parent_id = #{mediaTypeParentId,jdbcType=INTEGER}
and a.update_time <= DATE_FORMAT(#{updateTime,jdbcType=TIMESTAMP},'Y%-%m-%d %H:%i:%s')

select a.ad_seat_id from ad_activity_adseat a left join ad_activity b on a.activity_id = b.id where 1=1
and a.activity_id = #{activityId,jdbcType=INTEGER}
and b.user_id = #{userId,jdbcType=INTEGER}

select count(1)
from ad_activity_adseat
where ad_seat_id = #{adSeatId,jdbcType=INTEGER}

select id from ad_activity_adseat where 1=1
and activity_id = #{activityId,jdbcType=INTEGER}
and ad_seat_id in #{id,jdbcType=INTEGER}
