mapper.select:
select count(a.id) from ad_jiucuo_task a left join ad_jiucuo_task_feedback b on a.id = b.jiucuo_task_id left join ad_activity c on a.activity_id = c.id left join ad_activity_adseat d on a.activity_id = d.activity_id and a.ad_seat_id = d.ad_seat_id left join ad_seat_info e on d.ad_seat_id = e.id left join ad_media f on d.media_id = f.id where 1=1
and a.activity_id = #{activityId,jdbcType=INTEGER}
and a.status = #{status,jdbcType=INTEGER}
and c.start_time >= DATE_FORMAT(#{startDate,jdbcType=TIMESTAMP},'%Y-%m-%d')
and c.end_time <= DATE_FORMAT(#{endDate,jdbcType=TIMESTAMP},'%Y-%m-%d')

select a.id, a.activity_id, a.ad_seat_id, a.submit_time, a.user_id, a.status,a.reason,a.problem_status,a.create_time, a.update_time, b.pic_url1,c.activity_name,e.province,e.city,e.region,e.street,d.id as activity_adseat_id,d.media_id, f.media_name,e.`name` as ad_seat_name from ad_jiucuo_task a left join ad_jiucuo_task_feedback b on a.id = b.jiucuo_task_id left join ad_activity c on a.activity_id = c.id left join ad_activity_adseat d on a.activity_id = d.activity_id and a.ad_seat_id = d.ad_seat_id left join ad_seat_info e on d.ad_seat_id = e.id left join ad_media f on d.media_id = f.id where 1=1
and a.activity_id = #{activityId,jdbcType=INTEGER}
and a.status = #{status,jdbcType=INTEGER}
and c.start_time >= DATE_FORMAT(#{startDate,jdbcType=TIMESTAMP},'%Y-%m-%d')
and c.end_time <= DATE_FORMAT(#{endDate,jdbcType=TIMESTAMP},'%Y-%m-%d')
order by a.id desc
