and a.status = #{status,jdbcType=INTEGER}
and a.update_time <= DATE_FORMAT(#{updateTime,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%s')
and d.media_id = #{mediaId,jdbcType=INTEGER}
and a.company_id = #{companyId,jdbcType=INTEGER}
and a.monitor_date <= DATE_FORMAT(#{nowDate,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%s')
and date_add(a.monitor_date, interval (a.monitor_last_days) day) > DATE_FORMAT(#{nowDate,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%s')
) t having distance <= #{metre,jdbcType=DOUBLE} order by distance,update_time desc

