select t.*
,if(ISNULL(lon + lat), 9999999999, st_distance (point(lon, lat), point(#{lon,jdbcType=DOUBLE},#{lat,jdbcType=DOUBLE})) * 111195) AS distance
from ( select a.id, a.activity_id, a.activity_adseat_id, a.task_type,a.monitor_date,a.monitor_last_days, a.user_id, a.status,a.problem_status,a.sub_created,a.parent_id,a.parent_type, a.verify_time, a.create_time, a.update_time, a.assessor_id,a.assignor_id,a.assignor_time,a.task_point,a.task_money,a.company_id, b.activity_name, c.sample_pic_url,c.monitor_start,c.monitor_end,c.ad_seat_id, d.`name` as ad_seat_name,d.location as ad_seat_location,d.ad_code as ad_seat_code, d.lon, d.lat, d.region, d.street, d.province, d.city from ad_monitor_task a left join ad_activity b on a.activity_id = b.id left join ad_activity_adseat c on a.activity_adseat_id = c.id left join ad_seat_info d on c.ad_seat_id = d.id where 1=1
and a.status = #{status,jdbcType=INTEGER}
and d.province = #{province,jdbcType=BIGINT}
and d.city = #{city,jdbcType=BIGINT}
and a.update_time <= DATE_FORMAT(#{updateTime,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%s')
and a.company_id = #{companyId,jdbcType=INTEGER}
and a.monitor_date <= DATE_FORMAT(#{nowDate,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%s')
and date_add(a.monitor_date, interval (a.monitor_last_days) day) > DATE_FORMAT(#{nowDate,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%s')
) t
order by
distance,
update_time desc

