mapper.select:
	select
	count(a.id)
	from ad_seat_info as a
	left join ad_media as b on a.media_id=b.id
	left join ad_seat_type as c on a.ad_seat_type=c.id
	left join ad_crowd as d on d.ad_seat_id=a.id
	 where 1=1

      and a.province = #{province,jdbcType=INTEGER}


      and a.city = #{city,jdbcType=INTEGER}


      and a.region = #{region,jdbcType=INTEGER}


      and a.street = #{street,jdbcType=INTEGER}


      and b.id = #{mediaId,jdbcType=INTEGER}


      and d.sex = #{sex,jdbcType=INTEGER}


      and d.age_part = #{agePart,jdbcType=INTEGER}

	order by a.id asc

	select
		a.id,
		a.street,
		b.media_name,
		a.location,
		a.ad_size,
		c.name,
		a.pv
	from
		ad_seat_info as a
		left join ad_media as b on a.media_id=b.id
		left join ad_seat_type as c on a.ad_seat_type=c.id
		left join ad_crowd as d on d.ad_seat_id=a.id
	 where 1=1

      and a.province = #{province,jdbcType=INTEGER}


      and a.city = #{city,jdbcType=INTEGER}


      and a.region = #{region,jdbcType=INTEGER}


      and a.street = #{street,jdbcType=INTEGER}


      and b.id = #{mediaId,jdbcType=INTEGER}


      and d.sex = #{sex,jdbcType=INTEGER}


      and d.age_part = #{agePart,jdbcType=INTEGER}

	order by a.id asc
