mapper.select:
	select
		count(a.id)
	from ad_seat_info as a
	left join ad_media as b on a.media_id=b.id
	left join ad_media_type c on a.media_type_parent_id=c.id
	left join ad_media_type d on a.media_type_id=d.id
	 where 1=1

      and a.province = #{province,jdbcType=BIGINT}


      and a.city = #{city,jdbcType=BIGINT}


      and a.region = #{region,jdbcType=BIGINT}


      and a.street = #{street,jdbcType=BIGINT}


      and b.id = #{mediaId,jdbcType=INTEGER}


		and b.user_id = #{userId,jdbcType=INTEGER}


		and a.ad_code = #{adCode,jdbcType=VARCHAR}


		and a.id <> #{exceptId,jdbcType=INTEGER}


		and a.ad_code_flag <> #{codeFlag,jdbcType=INTEGER}

	order by a.id asc

	select
		a.id, a.name, a.province, a.city, a.region, a.street, a.media_id, a.ad_seat_type, a.ad_seat_type_text,
		a.media_type_parent_id, a.media_type_id, a.unique_key,
		a.ad_code, a.ad_code_url, a.location,
		a.ad_size, a.ad_area, a.pv, a.private_car_pv, a.bus_pv, a.lon, a.lat, a.map_standard,
		a.contact_name, a.contact_cell,
		a.create_time, a.update_time, a.memo, a.allow_multi, a.multi_num,
		b.media_name,
		c.name as parentName,
		d.name as secondName,
		a.ad_code_flag
	from
		ad_seat_info as a
	left join ad_media as b on a.media_id=b.id
	left join ad_media_type c on a.media_type_parent_id=c.id
	left join ad_media_type d on a.media_type_id=d.id
	 where 1=1

      and a.province = #{province,jdbcType=BIGINT}


      and a.city = #{city,jdbcType=BIGINT}


      and a.region = #{region,jdbcType=BIGINT}


      and a.street = #{street,jdbcType=BIGINT}


      and b.id = #{mediaId,jdbcType=INTEGER}


		and b.user_id = #{userId,jdbcType=INTEGER}


		and a.ad_code = #{adCode,jdbcType=VARCHAR}


		and a.id <> #{exceptId,jdbcType=INTEGER}

	order by a.id asc

select count(a.id)
from ad_seat_info a
where a.ad_code = #{adSeatCode,jdbcType=VARCHAR}

	select 
		count(1) count, city
	from ad_seat_info
	where 1=1

      and media_id = #{mediaId,jdbcType=INTEGER}


      and province = #{province,jdbcType=BIGINT}


      and city = #{city,jdbcType=BIGINT}


      and region = #{region,jdbcType=BIGINT}


    	and id in

	    	#{id}


	group by city

	select
		lon, lat, name
	from ad_seat_info
	where 1=1

      and media_id = #{mediaId,jdbcType=INTEGER}


      and province = #{province,jdbcType=BIGINT}


      and city = #{city,jdbcType=BIGINT}


      and region = #{region,jdbcType=BIGINT}


    	and id in

	    	#{id}

mapper.update:
		update ad_seat_info 
		set ad_code_flag = #{codeFlag,jdbcType=INTEGER}
    	where id = #{id,jdbcType=INTEGER}
