mapper.select:
	select count(1)
	from sys_user_message
	where 1=1
	
		and target_id = #{targetId,jdbcType=INTEGER}
	
	
		and target_user_id = #{targetUserId,jdbcType=INTEGER}
	
	
    and type = #{type,jdbcType=INTEGER}
  
  
    and content = #{content,jdbcType=VARCHAR}
  
  
    and is_finish = #{isFinish,jdbcType=INTEGER}

	  select a.id,a.target_id,a.target_user_id,a.type,a.content,a.is_finish,a.create_time,a.update_time
	  from sys_user_message a 
	  where 1=1
	
    and a.target_id = #{targetId,jdbcType=INTEGER}
  
  
    and a.target_user_id = #{targetUserId,jdbcType=INTEGER}
  
  
    and a.type = #{type,jdbcType=INTEGER}
  
  
    and a.content = #{content,jdbcType=VARCHAR}
  
  
    and a.is_finish = #{isFinish,jdbcType=INTEGER}
