mapper.insert:

insert into ad_user_money (id, user_id, money, 
  result, create_time, update_time
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{money,jdbcType=DOUBLE}, 
  #{result,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select
count(a.id)
from
ad_user_money a
left join sys_user_execute b on a.user_id=b.id
where 1=1

  and b.username like #{username,jdbcType=VARCHAR}


  and a.user_id = #{userId,jdbcType=INTEGER}


  and a.money = #{money,jdbcType=DOUBLE}
  

  and a.result = #{result,jdbcType=VARCHAR}


   and a.create_time = #{createTime,jdbcType=TIMESTAMP}


   and a.update_time <= DATE_FORMAT(#{updateTime,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%s')

select a.id id, a.user_id userId, a.money money, a.result result, a.create_time createTime, a.update_time updateTime, b.username username, (select sum(c.money) from ad_user_money c where c.user_id = a.user_id) sum from ad_user_money a left join sys_user_execute b on a.user_id=b.id where 1=1
and b.username like #{username,jdbcType=VARCHAR}
and a.user_id = #{userId,jdbcType=INTEGER}
and a.money = #{money,jdbcType=DOUBLE}
and a.result = #{result,jdbcType=VARCHAR}
and a.create_time = #{createTime,jdbcType=TIMESTAMP}
and a.update_time <= DATE_FORMAT(#{updateTime,jdbcType=TIMESTAMP},'%Y-%m-%d %H:%i:%s')
order by a.id desc

select sum(money) 
FROM ad_user_money 
where user_id=#{userId,jdbcType=INTEGER}
