mapper.update:
update sys_resources
set name=#{name,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select
count(id)
from
sys_resources
where 1=1

  and id = #{id,jdbcType=INTEGER}


  and name like #{name,jdbcType=VARCHAR}


  and parentId = #{parentid,jdbcType=INTEGER}


  and type = #{type,jdbcType=VARCHAR}


  and user_id = #{userId,jdbcType=INTEGER}


  and start_time >= DATE_FORMAT(#{startDate,jdbcType=TIMESTAMP},'%Y-%m-%d')


  and end_time <= DATE_FORMAT(#{endDate,jdbcType=TIMESTAMP},'%Y-%m-%d')

select a.id id, a.name name, a.parentId parentid, a.type type, a.user_id userId, a.department_type departmentType, a.create_time createTime, a.update_time updateTime, b.realname leaderName, c.name departmentName from sys_resources a left join sys_user b on a.user_id = b.id left join sys_resources c on a.parentId = c.id where 1=1
and a.id = #{id,jdbcType=INTEGER}
and a.name like #{name,jdbcType=VARCHAR}
and a.parentId = #{parentid,jdbcType=INTEGER}
and a.type = #{type,jdbcType=VARCHAR}
and a.user_id = #{userId,jdbcType=INTEGER}
and a.start_time >= DATE_FORMAT(#{startDate,jdbcType=TIMESTAMP},'%Y-%m-%d')
and a.end_time <= DATE_FORMAT(#{endDate,jdbcType=TIMESTAMP},'%Y-%m-%d')
order by a.id desc

select user_id 
from sys_resources
where department_type = #{departmentType,jdbcType=INTEGER}

select id
from sys_resources
where parentId=#{parentId,jdbcType=INTEGER}
and id=#{id,jdbcType=INTEGER}
