mapper.select:
select

from sys_user_execute
where 1=1

  and operate_id = #{operateId,jdbcType=INTEGER}


  and usertype = #{usertype,jdbcType=INTEGER}

select
count(1)
from sys_user_execute
where 1=1

  and operate_id = #{operateId,jdbcType=INTEGER}


  and usertype = #{usertype,jdbcType=INTEGER}


  and usertype in
  #{usertype,jdbcType=INTEGER}



  and (username like #{nameOrUsername,jdbcType=VARCHAR} or realname like #{nameOrUsername,jdbcType=VARCHAR})

select a.id, a.username, a.password, a.realname, a.mobile, a.company, a.head_img_url, a.usertype, a.operate_id, a.status, a.province, a.city, a.region, a.street, a.remark, a.login_ip, a.login_time, a.default_page, a.create_time, a.update_time, b.media_name,a.device_id,a.system_version, c.realname companyName from sys_user_execute a left join sys_user c on a.operate_id = c.id left join ad_media b on b.user_id = c.id where 1=1
and a.operate_id = #{operateId,jdbcType=INTEGER}
and a.usertype = #{usertype,jdbcType=INTEGER}
and a.usertype in #{usertype,jdbcType=INTEGER}
and (a.username like #{nameOrUsername,jdbcType=VARCHAR} or a.realname like #{nameOrUsername,jdbcType=VARCHAR})
order by a.id desc

mapper.update:

	update 
		sys_user_execute
    set 
	    device_id = #{deviceId,jdbcType=VARCHAR},
	    system_version= #{systemVersion,jdbcType=VARCHAR}
    where id = #{id,jdbcType=INTEGER}
